"use client"

import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"

// CSS styles
const styles = `
  .device-management {
    min-height: 100vh;
    background-color: #f9fafb;
    font-family: Arial, sans-serif;
  }

  .header {
    background: linear-gradient(to right, #4facfe, #00f2fe);
    color: white;
    padding: 24px;
    border-bottom-left-radius: 32px;
    border-bottom-right-radius: 32px;
    text-align: center;
  }

  .header h1 {
    font-size: 24px;
    font-weight: 600;
    margin-bottom: 8px;
  }

  .header p {
    font-size: 14px;
    opacity: 0.8;
  }

  .energy-consumption {
    background-color: white;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    border-radius: 12px;
    padding: 16px;
    margin: -32px 16px 0;
  }

  .energy-consumption h2 {
    font-size: 14px;
    font-weight: 600;
    color: #374151;
    margin-bottom: 16px;
  }

  .energy-data {
    display: flex;
    justify-content: space-between;
  }

  .energy-item {
    display: flex;
    align-items: center;
    gap: 8px;
  }

  .energy-icon {
    font-size: 24px;
  }

  .energy-label {
    font-size: 12px;
    color: #6b7280;
  }

  .energy-value {
    font-size: 16px;
    font-weight: 600;
    color: #111827;
  }

  .content {
    padding: 16px;
  }

  .section-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 16px;
  }

  .section-header h2 {
    font-size: 20px;
    font-weight: 600;
    color: #111827;
  }

  .add-button {
    font-size: 14px;
    color: #3b82f6;
    background: none;
    border: none;
    cursor: pointer;
  }

  .room-buttons {
    display: flex;
    gap: 12px;
    overflow-x: auto;
    padding-bottom: 8px;
    margin-bottom: 24px;
  }

  .room-button {
    padding: 8px 24px;
    border-radius: 9999px;
    font-size: 14px;
    border: none;
    cursor: pointer;
    white-space: nowrap;
    background-color: #f3f4f6;
    color: #4b5563;
  }

  .room-button.active {
    background-color: #4facfe;
    color: white;
  }

  .device-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 16px;
  }

  .device-card {
    background-color: #f3f4f6;
    border-radius: 12px;
    padding: 16px;
    transition: background-color 0.3s;
  }

  .device-card.on {
    background-color: #4facfe;
    color: white;
  }

  .device-header {
